    private void btnConvertirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnConvertirActionPerformed
        if(rbCelsiusKelvin.isSelected()){
            c.setValor(Double.parseDouble(txtValor.getText()));
            c.Celsius_Kelvin();
            txtResultado.setText(String.valueOf(c.getResultado()));
        }
        else if(rbKelvinCeslsius.isSelected()){
            k.setValor(Double.parseDouble(txtValor.getText()));
            k.Kelvin_Celsius();
            txtResultado.setText(String.valueOf(k.getResultado()));
        }
        else if(rbCelsiusFarenheit.isSelected()){
            c.setValor(Double.parseDouble(txtValor.getText()));
            c.Celsius_Farenheit();
            txtResultado.setText(String.valueOf(c.getResultado()));
        }
        else if(rbFarenheitCelsius.isSelected()){
            f.setValor(Double.parseDouble(txtValor.getText()));
            f.Farenheit_Celsius();
            txtResultado.setText(String.valueOf(f.getResultado()));
        }
        else if(rbFK.isSelected()){
            f.setValor(Double.parseDouble(txtValor.getText()));
            f.Farenheit_Kelvin();
            System.out.println(5/9);
            txtResultado.setText(String.valueOf(f.getResultado()));
        }
        else if(rbKF.isSelected()){
            k.setValor(Double.parseDouble(txtValor.getText()));
            k.Kelvin_Farenheit();
            txtResultado.setText(String.valueOf(k.getResultado()));
        }
        
    }//GEN-LAST:event_btnConvertirActionPerformed

